$(".temabanua-price").each(function () {
    var a = $(this),
        b = a.data("id");
    $.ajax({
        url: "/feeds/posts/default/" + b + "?alt=json",
        type: "get",
        dataType: "jsonp",
        success: function (x) {
            var f = x.entry.content.$t,
                p = $("<div>").html(f),
                v = p.find('strike:contains("price/")'),
                d = p.find('strike:contains("price2/")'),
                t = p.find('strike:contains("sales/")'),
                q = p.find('strike:contains("sale/")'),
                z = p.find('strike:contains("download/")'),
                k = p.find('strike:contains("trend/")');
            if (v.length > 0) {
                var g = v.text(),
                    m = g.split("/"),
                    h = m[1];
                a.find(".item-price").text(h).parent().addClass("show")
            }
            if (d.length > 0) {
                g = d.text(), m = g.split("/");
                var c = m[1];
                a.find(".itemOriginalPrice").text(c).parent().addClass("show")
            }
            if (t.length > 0) {
                g = t.text(), m = g.split("/");
                var w = m[1];
                a.find(".itemHomeSales").text(w).parent().addClass("show")
            }
            if (q.length > 0) {
                g = q.text(), m = g.split("/");
                var u = m[1];
                a.find(".temabanua_badge-sale").parent(u).addClass("show")
            }
            if (z.length > 0) {
                g = z.text(), m = g.split("/");
                var j = m[1];
                a.find(".itemHomeFree").text(j).parent().addClass("show")
            }
            if (k.length > 0) {
                g = k.text(), m = g.split("/");
                var y = m[1];
                a.find(".temabanua_badge-trend").parent(y).addClass("show")
            }
        }
    })
});
