function temabanuaPrice(a) {
    $(a).each(function () {
        var b = $(this),
            d = b.data("id");
        $.ajax({
            url: "/feeds/posts/default/" + d + "?alt=json",
            type: "get",
            dataType: "jsonp",
            success: function (y) {
                var k = y.entry.content.$t,
                    q = $("<div>").html(k),
                    w = q.find('strike:contains("price/")'),
                    h = q.find('strike:contains("price2/")'),
                    u = q.find('strike:contains("sales/")'),
                    t = q.find('strike:contains("sale/")'),
                    f = q.find('strike:contains("download/")'),
                    m = q.find('strike:contains("trend/")');
                if (w.length > 0) {
                    var K = w.text(),
                        p = K.split("/"),
                        c = p[1];
                    b.find(".item-price").text(c).parent().addClass("show")
                }
                if (h.length > 0) {
                    K = h.text(), p = K.split("/");
                    var g = p[1];
                    b.find(".itemOriginalPrice").text(g).parent().addClass("show")
                }
                if (u.length > 0) {
                    K = u.text(), p = K.split("/");
                    var x = p[1];
                    b.find(".itemHomeSales").text(x).parent().addClass("show")
                }
                if (t.length > 0) {
                    K = t.text(), p = K.split("/");
                    var v = p[1];
                    b.find(".temabanua_badge-sale").parent(v).addClass("show")
                }
                if (f.length > 0) {
                    K = f.text(), p = K.split("/");
                    var j = p[1];
                    b.find(".itemHomeFree").text(j).parent().addClass("show")
                }
                if (m.length > 0) {
                    K = m.text(), p = K.split("/");
                    var z = p[1];
                    b.find(".temabanua_badge-trend").parent(z).addClass("show")
                }
            }
        })
    })
}
$("#temabanua-load-more-link").each(function () {
    var c = $(this),
        d = c.data("load");
    d && c.show(), c.click(function (a) {
        a.preventDefault(), c.hide(), $.ajax({
            url: d,
            success: function (f) {
                var b = $(f).find(".blog-posts");
                b.find(".index-post"), $(".blog-posts").append(b.html()), (d = $(f).find("#temabanua-load-more-link").data("load")) ? c.show() : (c.hide(), $("#blog-pager .no-more").addClass("show"))
            },
            beforeSend: function () {
                $("#blog-pager .loading").show()
            },
            complete: function () {
                $("#blog-pager .loading").hide(), temabanuaPrice(".temabanua-price")
            }
        })
    })
});
